targets.expr:
count by (label_karpenter_sh_nodepool) (
  kube_pod_info{}
  * on (namespace, pod) group_left() (kube_pod_status_phase{phase="Running"} == 1)
  * on (node) group_left(label_karpenter_sh_nodepool)
    kube_node_labels{node=~"$node", node!~"^fargate-.*", label_karpenter_sh_nodepool=~"$nodepool"}
)

count by (label_karpenter_sh_nodepool) (
  kube_pod_info{}
  * on (namespace, pod) group_left() (kube_pod_status_phase{phase="Running"} == 1)
  * on (node) group_left(label_karpenter_sh_nodepool)
    kube_node_labels{node=~"$node", node=~"^fargate-.*", label_karpenter_sh_nodepool=~"$nodepool"}
)

sum by (label_karpenter_sh_nodepool) (
  node_total_hourly_cost{}
  * on (node) group_left(label_karpenter_sh_nodepool) (
    avg by (node, label_karpenter_sh_nodepool) (kube_node_labels{node=~"$node", node!~"fargate.*", label_karpenter_sh_nodepool=~"$nodepool"})
  )
)

(
# Normal nodes (node_expoter)
(sum(rate(
  node_cpu_seconds_total{mode!~"idle|iowait|steal"}
  * on(instance) group_left()
    (
      node_uname_info{}
      * on(nodename) group_left()
      label_replace(kube_node_labels{node=~"$node", fargate!~"^fargate-.*", label_karpenter_sh_nodepool=~"$nodepool"}, "nodename", "$1", "node", "(.*)")
    )
[$__rate_interval])) or vector(0))
+
# Fargate nodes (kube cAdvisor)
(sum(
  rate(
    container_cpu_usage_seconds_total{image!=""}
    * on(node) group_left()
    kube_node_labels{node=~"$node", node=~"^fargate-.+", label_karpenter_sh_nodepool=~"$nodepool"}
  [$__rate_interval])
) or vector(0))
)

/

(
# Normal nodes (kubelet)
(sum(
  machine_cpu_cores{}
  * on(node) group_left()
  kube_node_labels{node=~"$node", node!~"^fargate-.+", label_karpenter_sh_nodepool=~"$nodepool"}
) or vector(0))
+
# Fargate nodes (kube cAdvisor)
